class Solution:
    def rangeSum(self, nums: [int], n: int, left: int, right: int) -> int:
        sums = []
        for j in range(n):
            sum = 0
            for i in range(j, n):
                sum += nums[i]
                sums.append(sum)
        sums.sort()
        print(len(sums))
        res = 0
        for i in range(left-1, right):
            res += sums[i]
        return res % (10**9 + 7)

